import React from "react";

import Avatar from "@material-ui/core/Avatar";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import { useTheme } from "@material-ui/core/styles";

function LeaderboardEntry({
  data,
  onMouseOver,
  onMouseLeave,
  onClick,
  selectedUser,
  position,
}) {
  const theme = useTheme();

  return (
    <React.Fragment
    >
      <ListItem
        onMouseOver={onMouseOver}
        onMouseLeave={onMouseLeave}
        onClick={onClick}
        style={{
          padding: 0,
          display: "flex",
          flexDirection: "row",
          borderRadius: "5px",
          alignContent: "stretch",
          alignItems: "stretch",
          marginTop: "0px",
          cursor: "pointer",
          backgroundColor:
            selectedUser && selectedUser.username === data.username
              ? theme.palette.action.selected
              : "initial",
        }}
      >
        <ListItemAvatar
          style={{
            display: "flex",
            alignContent: "center",
            alignItems: "center",
            marginLeft: "8px",
          }}
        >
          <Avatar>
            <img
              style={{
                maxWidth: "100%",
              }}
              src={
                data.avatar_url ||
                "data:image/png;base64,"
              }
              alt=""
            />
          </Avatar>
        </ListItemAvatar>
        <ListItemText
          primaryTypographyProps={{ style: theme.classes.participantName }}
          secondaryTypographyProps={{
            style: {...theme.classes.participantPerformance, color:theme.palette.text.primary},
          }}
          primary={position + ". " + data.name}
          secondary={String(data.distance || 0).replace(".", ",") + " km"}
        />
      </ListItem>
    </React.Fragment>
  );
}

export default LeaderboardEntry;
